   "source": [
    "# %% [markdown]\n",
    "# # Analyse de Fraude de Cartes de Cr√©dit\n",
    "# ## Chargement des donn√©es depuis HDFS vers PostgreSQL\n",
    "\n",
    "# %%\n",
    "# 1. Installation des d√©pendances n√©cessaires\n",
    "!pip install psycopg2-binary pandas numpy matplotlib seaborn -q\n",
    "\n",
    "# %%\n",
    "# 2. Initialisation de Spark avec support PostgreSQL\n",
    "from pyspark.sql import SparkSession\n",
    "from pyspark.sql.functions import *\n",
    "from pyspark.sql.types import *\n",
    "import pyspark.sql.functions as F\n",
    "\n",
    "# Cr√©ation de la session Spark\n",
    "spark = SparkSession.builder \\\n",
    "    .appName(\"CreditCardFraudAnalysis\") \\\n",
    "    .config(\"spark.jars\", \"/usr/local/spark/jars/postgresql-42.5.0.jar\") \\\n",
    "    .config(\"spark.executor.memory\", \"2g\") \\\n",
    "    .config(\"spark.driver.memory\", \"2g\") \\\n",
    "    .getOrCreate()\n",
    "\n",
    "print(\"‚úÖ Spark Session initialis√©e\")\n",
    "\n",
    "# %%\n",
    "# 3. Configuration PostgreSQL\n",
    "POSTGRES_HOST = \"host.docker.internal\"  # Pour Docker sous Windows\n",
    "# Alternative: utiliser l'IP du conteneur PostgreSQL\n",
    "POSTGRES_PORT = \"5432\"\n",
    "POSTGRES_DB = \"fraud_db\"\n",
    "POSTGRES_USER = \"postgres\"\n",
    "POSTGRES_PASSWORD = \"postgres\"\n",
    "POSTGRES_URL = f\"jdbc:postgresql://{POSTGRES_HOST}:{POSTGRES_PORT}/{POSTGRES_DB}\"\n",
    "\n",
    "print(f\"üì° Configuration PostgreSQL: {POSTGRES_URL}\")\n",
    "\n",
    "# %%\n",
    "# 4. Test de connexion √† PostgreSQL\n",
    "import psycopg2\n",
    "try:\n",
    "    conn = psycopg2.connect(\n",
    "        host=\"host.docker.internal\",\n",
    "        database=POSTGRES_DB,\n",
    "        user=POSTGRES_USER,\n",
    "        password=POSTGRES_PASSWORD,\n",
    "        port=POSTGRES_PORT\n",
    "    )\n",
    "    cursor = conn.cursor()\n",
    "    cursor.execute(\"SELECT version();\")\n",
    "    version = cursor.fetchone()\n",
    "    print(f\"‚úÖ Connexion PostgreSQL r√©ussie: {version[0]}\")\n",
    "    conn.close()\n",
    "except Exception as e:\n",
    "    print(f\"‚ö†Ô∏è Erreur de connexion PostgreSQL: {e}\")\n",
    "    print(\"Essai avec 'localhost'...\")\n",
    "    POSTGRES_HOST = \"localhost\"\n",
    "    POSTGRES_URL = f\"jdbc:postgresql://{POSTGRES_HOST}:{POSTGRES_PORT}/{POSTGRES_DB}\"\n",
    "\n",
    "# %%\n",
    "# 5. Chargement des donn√©es depuis HDFS\n",
    "print(\"üìÅ Chargement des donn√©es depuis HDFS...\")\n",
    "try:\n",
    "    df = spark.read.csv(\n",
    "        \"hdfs://namenode:8020/data/creditcard.csv\",\n",
    "        header=True,\n",
    "        inferSchema=True\n",
    "    )\n",
    "    \n",
    "    print(f\"‚úÖ Donn√©es charg√©es avec succ√®s!\")\n",
    "    print(f\"üìä Nombre de lignes: {df.count():,}\")\n",
    "    print(f\"üìä Nombre de colonnes: {len(df.columns)}\")\n",
    "    \n",
    "except Exception as e:\n",
    "    print(f\"‚ùå Erreur lors du chargement: {e}\")\n",
    "    print(\"Essai avec le chemin local...\")\n",
    "    df = spark.read.csv(\n",
    "        \"/home/jovyan/data/creditcard.csv\",\n",
    "        header=True,\n",
    "        inferSchema=True\n",
    "    )\n",
    "\n",
    "# %%\n",
    "# 6. Aper√ßu des donn√©es\n",
    "print(\"\\nüîç Aper√ßu des donn√©es:\")\n",
    "df.show(5, truncate=True)\n",
    "\n",
    "print(\"\\nüìã Sch√©ma des donn√©es:\")\n",
    "df.printSchema()\n",
    "\n",
    "# %%\n",
    "# 7. Statistiques descriptives\n",
    "print(\"üìà Statistiques descriptives:\")\n",
    "\n",
    "# Distribution des classes\n",
    "class_distribution = df.groupBy(\"Class\").count()\n",
    "print(\"Distribution des classes (0=Normal, 1=Fraude):\")\n",
    "class_distribution.show()\n",
    "\n",
    "# Pourcentage de fraude\n",
    "total = df.count()\n",
    "fraud_count = df.filter(col(\"Class\") == 1).count()\n",
    "fraud_percentage = (fraud_count / total) * 100\n",
    "\n",
    "print(f\"\\nüìä Statistiques globales:\")\n",
    "print(f\"Total transactions: {total:,}\")\n",
    "print(f\"Transactions frauduleuses: {fraud_count:,}\")\n",
    "print(f\"Taux de fraude: {fraud_percentage:.4f}%\")\n",
    "\n",
    "# %%\n",
    "# 8. Analyse des montants\n",
    "print(\"\\nüí∞ Analyse des montants:\")\n",
    "amount_stats = df.select(\n",
    "    F.mean(\"Amount\").alias(\"Moyenne\"),\n",
    "    F.stddev(\"Amount\").alias(\"√âcart-type\"),\n",
    "    F.min(\"Amount\").alias(\"Minimum\"),\n",
    "    F.max(\"Amount\").alias(\"Maximum\")\n",
    ").collect()[0]\n",
    "\n",
    "print(f\"Moyenne du montant: ${amount_stats['Moyenne']:.2f}\")\n",
    "print(f\"√âcart-type: ${amount_stats['√âcart-type']:.2f}\")\n",
    "print(f\"Montant minimum: ${amount_stats['Minimum']:.2f}\")\n",
    "print(f\"Montant maximum: ${amount_stats['Maximum']:.2f}\")\n",
    "\n",
    "# Comparaison montants fraude vs normal\n",
    "fraud_amount_stats = df.filter(col(\"Class\") == 1).select(\n",
    "    F.mean(\"Amount\").alias(\"Moyenne_fraude\"),\n",
    "    F.stddev(\"Amount\").alias(\"Std_fraude\")\n",
    ").collect()[0]\n",
    "\n",
    "normal_amount_stats = df.filter(col(\"Class\") == 0).select(\n",
    "    F.mean(\"Amount\").alias(\"Moyenne_normal\"),\n",
    "    F.stddev(\"Amount\").alias(\"Std_normal\")\n",
    ").collect()[0]\n",
    "\n",
    "print(f\"\\nüîÑ Comparaison montants:\")\n",
    "print(f\"Moyenne transactions normales: ${normal_amount_stats['Moyenne_normal']:.2f}\")\n",
    "print(f\"Moyenne transactions frauduleuses: ${fraud_amount_stats['Moyenne_fraude']:.2f}\")\n",
    "\n",
    "# %%\n",
    "# 9. Pr√©paration des donn√©es pour PostgreSQL\n",
    "print(\"\\nüõ†Ô∏è Pr√©paration des tables pour PostgreSQL...\")\n",
    "\n",
    "# Table 1: Distribution des classes\n",
    "table1 = df.groupBy(\"Class\").agg(\n",
    "    F.count(\"*\").alias(\"count\"),\n",
    "    F.round(F.mean(\"Amount\"), 2).alias(\"avg_amount\"),\n",
    "    F.round(F.stddev(\"Amount\"), 2).alias(\"std_amount\"),\n",
    "    F.round(F.min(\"Amount\"), 2).alias(\"min_amount\"),\n",
    "    F.round(F.max(\"Amount\"), 2).alias(\"max_amount\")\n",
    ").withColumnRenamed(\"Class\", \"class_type\")\n",
    "\n",
    "print(\"Table 1 - Distribution des classes:\")\n",
    "table1.show()\n",
    "\n",
    "# %%\n",
    "# Table 2: Transactions par heure\n",
    "table2 = df.withColumn(\"hour\", (col(\"Time\") / 3600).cast(\"int\")) \\\n",
    "    .groupBy(\"hour\", \"Class\") \\\n",
    "    .agg(\n",
    "        F.count(\"*\").alias(\"transaction_count\"),\n",
    "        F.round(F.sum(\"Amount\"), 2).alias(\"total_amount\"),\n",
    "        F.round(F.mean(\"Amount\"), 2).alias(\"avg_amount\"),\n",
    "        F.round(F.stddev(\"Amount\"), 2).alias(\"std_amount\")\n",
    "    ) \\\n",
    "    .orderBy(\"hour\") \\\n",
    "    .limit(100)  # Limiter pour l'affichage\n",
    "\n",
    "print(\"Table 2 - Transactions par heure (premi√®res 100 lignes):\")\n",
    "table2.show(10)\n",
    "\n",
    "# %%\n",
    "# Table 3: Analyse par cat√©gorie de montant\n",
    "# Cr√©ation des cat√©gories de montant\n",
    "amount_bins = F.when(col(\"Amount\") < 10, \"0-10\") \\\n",
    "    .when((col(\"Amount\") >= 10) & (col(\"Amount\") < 50), \"10-50\") \\\n",
    "    .when((col(\"Amount\") >= 50) & (col(\"Amount\") < 100), \"50-100\") \\\n",
    "    .when((col(\"Amount\") >= 100) & (col(\"Amount\") < 500), \"100-500\") \\\n",
    "    .when((col(\"Amount\") >= 500) & (col(\"Amount\") < 1000), \"500-1000\") \\\n",
    "    .otherwise(\"1000+\")\n",
    "\n",
    "table3 = df.withColumn(\"amount_range\", amount_bins) \\\n",
    "    .groupBy(\"amount_range\", \"Class\") \\\n",
    "    .agg(\n",
    "        F.count(\"*\").alias(\"count\"),\n",
    "        F.round(F.mean(\"Amount\"), 2).alias(\"avg_amount\"),\n",
    "        F.round(F.min(\"Amount\"), 2).alias(\"min_amount\"),\n",
    "        F.round(F.max(\"Amount\"), 2).alias(\"max_amount\")\n",
    "    ) \\\n",
    "    .orderBy(\"amount_range\")\n",
    "\n",
    "print(\"Table 3 - Analyse par cat√©gorie de montant:\")\n",
    "table3.show()\n",
    "\n",
    "# %%\n",
    "# Table 4: D√©tails des fraudes\n",
    "table4 = df.filter(col(\"Class\") == 1) \\\n",
    "    .select(\n",
    "        \"Time\",\n",
    "        \"V1\", \"V2\", \"V3\", \"V4\", \"V5\", \"V6\", \"V7\", \"V8\", \"V9\", \"V10\",\n",
    "        \"V11\", \"V12\", \"V13\", \"V14\", \"V15\", \"V16\", \"V17\", \"V18\", \"V19\", \"V20\",\n",
    "        \"V21\", \"V22\", \"V23\", \"V24\", \"V25\", \"V26\", \"V27\", \"V28\",\n",
    "        \"Amount\",\n",
    "        \"Class\"\n",
    "    ) \\\n",
    "    .withColumn(\"fraud_id\", F.monotonically_increasing_id()) \\\n",
    "    .limit(500)  # Limiter √† 500 fraudes pour la base de donn√©es\n",
    "\n",
    "print(\"Table 4 - D√©tails des transactions frauduleuses:\")\n",
    "table4.show(5)\n",
    "\n",
    "# %%\n",
    "# Table 5: Statistiques globales\n",
    "table5 = df.agg(\n",
    "    F.count(\"*\").alias(\"total_transactions\"),\n",
    "    F.sum(F.when(col(\"Class\") == 1, 1).otherwise(0)).alias(\"total_fraud\"),\n",
    "    F.round(F.mean(\"Amount\"), 2).alias(\"global_avg_amount\"),\n",
    "    F.round(F.stddev(\"Amount\"), 2).alias(\"global_std_amount\"),\n",
    "    F.round(F.min(\"Amount\"), 2).alias(\"min_amount\"),\n",
    "    F.round(F.max(\"Amount\"), 2).alias(\"max_amount\"),\n",
    "    F.round((F.sum(F.when(col(\"Class\") == 1, 1).otherwise(0)) / F.count(\"*\")) * 100, 4).alias(\"fraud_percentage\")\n",
    ")\n",
    "\n",
    "print(\"Table 5 - Statistiques globales:\")\n",
    "table5.show()\n",
    "\n",
    "# %%\n",
    "# 10. √âcriture dans PostgreSQL\n",
    "print(\"\\nüíæ √âcriture des donn√©es dans PostgreSQL...\")\n",
    "\n",
    "# Propri√©t√©s de connexion\n",
    "properties = {\n",
    "    \"user\": POSTGRES_USER,\n",
    "    \"password\": POSTGRES_PASSWORD,\n",
    "    \"driver\": \"org.postgresql.Driver\"\n",
    "}\n",
    "\n",
    "# Fonction pour √©crire dans PostgreSQL avec v√©rification\n",
    "def write_to_postgres(df, table_name, mode=\"overwrite\"):\n",
    "    try:\n",
    "        # √âcrire dans PostgreSQL\n",
    "        df.write \\\n",
    "            .mode(mode) \\\n",
    "            .option(\"truncate\", \"true\") \\\n",
    "            .option(\"batchsize\", 10000) \\\n",
    "            .jdbc(POSTGRES_URL, table_name, properties=properties)\n",
    "        \n",
    "        # V√©rifier le nombre de lignes √©crites\n",
    "        count = df.count()\n",
    "        print(f\"‚úÖ Table '{table_name}' √©crite: {count:,} lignes\")\n",
    "        return True\n",
    "        \n",
    "    except Exception as e:\n",
    "        print(f\"‚ùå Erreur lors de l'√©criture de '{table_name}': {e}\")\n",
    "        return False\n",
    "\n",
    "# %%\n",
    "# √âcrire toutes les tables\n",
    "tables_to_write = [\n",
    "    (table1, \"class_distribution\"),\n",
    "    (table2, \"hourly_transactions\"),\n",
    "    (table3, \"amount_analysis\"),\n",
    "    (table4, \"fraud_details\"),\n",
    "    (table5, \"global_stats\")\n",
    "]\n",
    "\n",
    "print(\"D√©but de l'√©criture dans PostgreSQL...\")\n",
    "success_count = 0\n",
    "\n",
    "for df_table, table_name in tables_to_write:\n",
    "    if write_to_postgres(df_table, table_name):\n",
    "        success_count += 1\n",
    "\n",
    "print(f\"\\nüìä {success_count}/{len(tables_to_write)} tables √©crites avec succ√®s!\")\n",
    "\n",
    "# %%\n",
    "# 11. Cr√©ation d'une vue pour Grafana\n",
    "print(\"\\nüìà Cr√©ation d'une vue optimis√©e pour Grafana...\")\n",
    "\n",
    "try:\n",
    "    # Lire les transactions horaires depuis PostgreSQL\n",
    "    hourly_df = spark.read \\\n",
    "        .jdbc(POSTGRES_URL, \"hourly_transactions\", properties=properties)\n",
    "    \n",
    "    # Cr√©er une vue enrichie\n",
    "    grafana_view = hourly_df \\\n",
    "        .groupBy(\"hour\") \\\n",
    "        .agg(\n",
    "            F.sum(\"transaction_count\").alias(\"total_transactions\"),\n",
    "            F.sum(\"total_amount\").alias(\"hourly_total_amount\"),\n",
    "            F.sum(F.when(col(\"Class\") == 1, col(\"transaction_count\")).otherwise(0)).alias(\"fraud_count\"),\n",
    "            F.sum(F.when(col(\"Class\") == 1, col(\"total_amount\")).otherwise(0)).alias(\"fraud_amount\")\n",
    "        ) \\\n",
    "        .withColumn(\"fraud_rate\", \n",
    "                   F.when(col(\"total_transactions\") > 0,\n",
    "                         F.round((col(\"fraud_count\") / col(\"total_transactions\")) * 100, 4))\n",
    "                   .otherwise(0)) \\\n",
    "        .withColumn(\"avg_transaction_amount\", \n",
    "                   F.round(col(\"hourly_total_amount\") / col(\"total_transactions\"), 2)) \\\n",
    "        .withColumn(\"avg_fraud_amount\",\n",
    "                   F.when(col(\"fraud_count\") > 0,\n",
    "                         F.round(col(\"fraud_amount\") / col(\"fraud_count\"), 2))\n",
    "                   .otherwise(0)) \\\n",
    "        .orderBy(\"hour\") \\\n",
    "        .select(\n",
    "            \"hour\",\n",
    "            \"total_transactions\",\n",
    "            \"hourly_total_amount\",\n",
    "            \"fraud_count\",\n",
    "            \"fraud_amount\",\n",
    "            \"fraud_rate\",\n",
    "            \"avg_transaction_amount\",\n",
    "            \"avg_fraud_amount\"\n",
    "        )\n",
    "    \n",
    "    print(\"Vue Grafana cr√©√©e - Aper√ßu:\")\n",
    "    grafana_view.show(10)\n",
    "    \n",
    "    # √âcrire la vue dans PostgreSQL\n",
    "    write_to_postgres(grafana_view, \"grafana_dashboard\")\n",
    "    \n",
    "except Exception as e:\n",
    "    print(f\"‚ö†Ô∏è Erreur lors de la cr√©ation de la vue Grafana: {e}\")\n",
    "\n",
    "# %%\n",
    "# 12. V√©rification finale\n",
    "print(\"\\nüîç V√©rification finale des donn√©es dans PostgreSQL...\")\n",
    "\n",
    "try:\n",
    "    # Lire et afficher un √©chantillon de chaque table\n",
    "    tables_to_check = [\"class_distribution\", \"hourly_transactions\", \n",
    "                      \"amount_analysis\", \"global_stats\", \"grafana_dashboard\"]\n",
    "    \n",
    "    for table in tables_to_check:\n",
    "        try:\n",
    "            df_check = spark.read \\\n",
    "                .jdbc(POSTGRES_URL, table, properties=properties)\n",
    "            count = df_check.count()\n",
    "            print(f\"üìã Table '{table}': {count:,} lignes\")\n",
    "            if count < 10:  # Afficher si petite table\n",
    "                df_check.show()\n",
    "        except:\n",
    "            print(f\"‚ö†Ô∏è Table '{table}' non accessible\")\n",
    "    \n",
    "    print(\"\\n‚úÖ V√©rification termin√©e!\")\n",
    "    \n",
    "except Exception as e:\n",
    "    print(f\"‚ö†Ô∏è Erreur lors de la v√©rification: {e}\")\n",
    "\n",
    "# %%\n",
    "# 13. Visualisation des donn√©es (optionnel)\n",
    "print(\"\\nüìä G√©n√©ration de visualisations...\")\n",
    "\n",
    "try:\n",
    "    import matplotlib.pyplot as plt\n",
    "    import seaborn as sns\n",
    "    import pandas as pd\n",
    "    \n",
    "    # Convertir les donn√©es Spark en Pandas pour la visualisation\n",
    "    class_dist_pd = table1.toPandas()\n",
    "    amount_analysis_pd = table3.filter(col(\"Class\") == 1).toPandas()\n",
    "    \n",
    "    # Figure 1: Distribution des classes\n",
    "    fig, axes = plt.subplots(1, 2, figsize=(14, 5))\n",
    "    \n",
    "    # Pie chart\n",
    "    axes[0].pie(class_dist_pd['count'], \n",
    "                labels=['Normal', 'Fraude'], \n",
    "                autopct='%1.3f%%',\n",
    "                colors=['lightgreen', 'lightcoral'])\n",
    "    axes[0].set_title('Distribution des Transactions')\n",
    "    \n",
    "    # Bar chart\n",
    "    axes[1].bar(['Normal', 'Fraude'], class_dist_pd['count'], \n",
    "                color=['lightgreen', 'lightcoral'])\n",
    "    axes[1].set_title('Nombre de Transactions par Type')\n",
    "    axes[1].set_ylabel('Nombre')\n",
    "    \n",
    "    plt.tight_layout()\n",
    "    plt.show()\n",
    "    \n",
    "    # Figure 2: Montants par cat√©gorie (fraudes seulement)\n",
    "    if not amount_analysis_pd.empty:\n",
    "        plt.figure(figsize=(10, 6))\n",
    "        bars = plt.bar(amount_analysis_pd['amount_range'], \n",
    "                      amount_analysis_pd['count'],\n",
    "                      color='lightcoral')\n",
    "        plt.title('Distribution des Fraudes par Cat√©gorie de Montant')\n",
    "        plt.xlabel('Cat√©gorie de Montant')\n",
    "        plt.ylabel('Nombre de Fraudes')\n",
    "        plt.xticks(rotation=45)\n",
    "        \n",
    "        # Ajouter les valeurs sur les barres\n",
    "        for bar in bars:\n",
    "            height = bar.get_height()\n",
    "            plt.text(bar.get_x() + bar.get_width()/2., height,\n",
    "                    f'{int(height)}', ha='center', va='bottom')\n",
    "        \n",
    "        plt.tight_layout()\n",
    "        plt.show()\n",
    "    \n",
    "    print(\"‚úÖ Visualisations g√©n√©r√©es avec succ√®s!\")\n",
    "    \n",
    "except Exception as e:\n",
    "    print(f\"‚ö†Ô∏è Erreur lors de la visualisation: {e}\")\n",
    "\n",
    "# %%\n",
    "# 14. Nettoyage et conclusion\n",
    "print(\"\\n\" + \"=\"*60)\n",
    "print(\"üéâ ANALYSE TERMIN√âE AVEC SUCC√àS!\")\n",
    "print(\"=\"*60)\n",
    "\n",
    "print(\"\\nüìã R√âSUM√â DU TRAITEMENT:\")\n",
